import styled from 'styled-components';

export const Section = styled.section`
    width: 400px;
    background: rgba(255,255,255,.70);
    border-radius: 10px;
    border-top: 5px solid #36BFB3;
    border-bottom: 5px solid #36BFB3;
    border-right:5px solid rgb(71 79 154);
    border-left: 5px solid rgb(71 79 154);
    margin-top: 15=px;

    @media (max-width: 430px) {
        width: 300px;
    } 
`
